mapper.update:
update culturalrelics
set culturalrelics_access = culturalrelics_access + 1
where culturalrelics_id = #{culturalrelicsId,jdbcType=INTEGER}

update culturalrelics
set culturalrelics_zan = culturalrelics_zan + 1
where culturalrelics_id = #{culturalrelicsId,jdbcType=INTEGER}

update culturalrelics
set culturalrelics_zan = culturalrelics_zan - 1
where culturalrelics_id = #{culturalrelicsId,jdbcType=INTEGER}
